// <!-- You are asked to develop a React App as follows:

// 1) Create a new CLASS component, name it YournameComp to the program that display’s your name, and extract the current Date and Time and display it on the page.

// 2) Add a CSS to change the text style align-center and the background color to green -->

import React, { Component } from 'react';
import { render } from 'react-dom';

import './VladComp.css';
export default class VladComp extends Component {
  constructor() {
    this.state = {
      currentDateTime: Date().toLocaleString(),
    };
  }
  render() {
    return (
      <div>
        Vlado Tomovic
        <p>{this.state.currentDateTime}</p>
      </div>
    );
  }
}

